function format1(a){return a.toFixed().replace(/./g,function(b,a,c){return 0<a&&"."!==b&&0===(c.length-a)%3?"."+b:b})}
function formatoRut(a){var b=a.value.replace(/\./g,"");b=b.replace(/-/g,"");cuerpo=b.slice(0,-1);dv=b.slice(-1).toUpperCase();a.value=0===cuerpo.length?cuerpo+"-"+dv:Number.isNaN(cuerpo)?cuerpo+"-"+dv:format1(Number.parseInt(cuerpo))+"-"+dv;if(6>cuerpo.length)return a.setCustomValidity("RUT Incompleto"),!1;if(14<cuerpo.length)return a.setCustomValidity("RUT demasiado largo"),!1;a.setCustomValidity("")};